import React, { useState } from "react";
import {  Routes, Route, Link } from "react-router-dom";


export default function AdminDashboard() {
  // ✅ Mock data (replace with API later)
  const [employees, setEmployees] = useState([
    { id: 1, name: "Alice Employee", role: "Employee", department: "HR" },
    { id: 2, name: "Bob Employee", role: "Employee", department: "Finance" },
  ]);

  const [managers, setManagers] = useState([
    { id: 1, name: "Charlie Manager", role: "Manager", department: "Engineering" },
    { id: 2, name: "Diana Manager", role: "Manager", department: "Sales" },
  ]);

  const [managerLeaveRequests, setManagerLeaveRequests] = useState([
    { id: 1, manager: "Charlie Manager", type: "Casual", from: "2025-08-22", to: "2025-08-23", remarks: "Family event", status: "Pending" },
    { id: 2, manager: "Diana Manager", type: "Earned", from: "2025-08-25", to: "2025-09-05", remarks: "Vacation", status: "Pending" },
  ]);

  const [exitRequests, setExitRequests] = useState([
    { id: 1, employee: "Alice", exitDate: "2025-09-01", remarks: "Moving abroad", status: "Pending" },
    { id: 2, employee: "Bob", exitDate: "2025-09-10", remarks: "Higher studies", status: "Pending" },
  ]);

  const [attendance, setAttendance] = useState([
    { id: 1, employee: "Alice", checkIn: "09:10", checkOut: "17:45" },
    { id: 2, employee: "Bob", checkIn: "-", checkOut: "-", status: "On Leave" },
    { id: 3, employee: "Charlie", checkIn: "09:00", checkOut: "18:05" },
  ]);

  // ✅ Actions
  const handleExitAction = (id, action) => {
    setExitRequests((prev) =>
      prev.map((req) =>
        req.id === id ? { ...req, status: action } : req
      )
    );
  };

  const handleManagerLeaveAction = (id, action) => {
    setManagerLeaveRequests((prev) =>
      prev.map((req) =>
        req.id === id ? { ...req, status: action } : req
      )
    );
  };

  return (
   
      <div style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
        
        {/* ✅ Navbar */}
        <nav style={{ 
          display: "flex", 
          gap: 20, 
          padding: "12px 20px", 
          background: "#1e40af", 
          color: "#fff" 
        }}>
         <nav>
          <Link style={linkStyle} to="/admindashboard/employees">  Employees  </Link>
        <Link style={linkStyle} to="/admindashboard/managers">  Managers  </Link>
        <Link style={linkStyle} to="/admindashboard/leave-requests">  Manager Leave Requests  </Link>
        <Link style={linkStyle} to="/admindashboard/exit-requests">  Exit Requests  </Link>
        <Link style={linkStyle} to="/admindashboard/attendance">  Attendance  </Link>
        </nav>
        </nav>

        <main style={{ flex: 1, padding: 20, background: "#f9fafb" }}>
          <Routes>
      <Route path="employees" element={<Employees employees={employees} />} />
      <Route path="managers" element={<Managers managers={managers} />} />
      <Route path="leave-requests" element={<ManagerLeaveRequests requests={managerLeaveRequests} onAction={handleManagerLeaveAction} />} />
      <Route path="exit-requests" element={<ExitRequests requests={exitRequests} onAction={handleExitAction} />} />
      <Route path="attendance" element={<Attendance attendance={attendance} />} />
      <Route path="*" element={<div>Welcome Admin! Choose a section from the navbar.</div>} />
          </Routes>
        </main>
      </div>
  
  );
}

const linkStyle = {
  color: "#fff",
  textDecoration: "none",
  fontWeight: "500"
};

// ✅ Subcomponents

function Employees({ employees }) {
  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Employees</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Name</th>
            <th className="p-2 border">Role</th>
            <th className="p-2 border">Department</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((emp) => (
            <tr key={emp.id}>
              <td className="p-2 border">{emp.name}</td>
              <td className="p-2 border">{emp.role}</td>
              <td className="p-2 border">{emp.department}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function Managers({ managers }) {
  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Managers</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Name</th>
            <th className="p-2 border">Role</th>
            <th className="p-2 border">Department</th>
          </tr>
        </thead>
        <tbody>
          {managers.map((mgr) => (
            <tr key={mgr.id}>
              <td className="p-2 border">{mgr.name}</td>
              <td className="p-2 border">{mgr.role}</td>
              <td className="p-2 border">{mgr.department}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function ManagerLeaveRequests({ requests, onAction }) {
  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Manager Leave Requests</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Manager</th>
            <th className="p-2 border">Type</th>
            <th className="p-2 border">From</th>
            <th className="p-2 border">To</th>
            <th className="p-2 border">Remarks</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Action</th>
          </tr>
        </thead>
        <tbody>
          {requests.map((req) => (
            <tr key={req.id}>
              <td className="p-2 border">{req.manager}</td>
              <td className="p-2 border">{req.type}</td>
              <td className="p-2 border">{req.from}</td>
              <td className="p-2 border">{req.to}</td>
              <td className="p-2 border">{req.remarks}</td>
              <td className="p-2 border">{req.status}</td>
              <td className="p-2 border space-x-2">
                <button
                  className="bg-green-600 text-white px-2 py-1 rounded"
                  onClick={() => onAction(req.id, "Approved")}
                >
                  Approve
                </button>
                <button
                  className="bg-red-600 text-white px-2 py-1 rounded"
                  onClick={() => onAction(req.id, "Rejected")}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function ExitRequests({ requests, onAction }) {
  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Exit Requests</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Employee</th>
            <th className="p-2 border">Exit Date</th>
            <th className="p-2 border">Remarks</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Action</th>
          </tr>
        </thead>
        <tbody>
          {requests.map((req) => (
            <tr key={req.id}>
              <td className="p-2 border">{req.employee}</td>
              <td className="p-2 border">{req.exitDate}</td>
              <td className="p-2 border">{req.remarks}</td>
              <td className="p-2 border">{req.status}</td>
              <td className="p-2 border space-x-2">
                <button
                  className="bg-green-600 text-white px-2 py-1 rounded"
                  onClick={() => onAction(req.id, "Approved")}
                >
                  Approve
                </button>
                <button
                  className="bg-red-600 text-white px-2 py-1 rounded"
                  onClick={() => onAction(req.id, "Rejected")}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function Attendance({ attendance }) {
  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Overall Attendance</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Employee</th>
            <th className="p-2 border">Check In</th>
            <th className="p-2 border">Check Out</th>
            <th className="p-2 border">Status</th>
          </tr>
        </thead>
        <tbody>
          {attendance.map((att) => (
            <tr key={att.id}>
              <td className="p-2 border">{att.employee}</td>
              <td className="p-2 border">{att.checkIn}</td>
              <td className="p-2 border">{att.checkOut}</td>
              <td className="p-2 border">{att.status || "Present"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
