/**
 * 
 */
import React, { useEffect, useState } from "react";
import axios from "axios";

const LeaveBalance = ({ employeeId }) => {
  const [balance, setBalance] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchLeaveBalance();
  }, [employeeId]);

  const fetchLeaveBalance = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/leaves/leaveBalance?employeeId=${employeeId}`, {
        withCredentials: true, // important if session/cookies are used
      });
      setBalance(res.data);
    } catch (err) {
      console.error("Error fetching leave balance:", err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Loading leave balance...</p>;
  if (!balance) return <p>No leave balance found.</p>;

  return (
    <div className="p-2 mb-3 bg-gray-50 rounded border border-gray-200">
  <h3 className="text-sm font-semibold mb-2">Leave Balance</h3>
  <div className="grid grid-cols-4 gap-2 text-center text-sm">
    <div>
      <p className="font-medium">Casual</p>
      <p className="text-blue-600">{balance.casual_LEAVE}</p>
    </div>
    <div>
      <p className="font-medium">Earned</p>
      <p className="text-green-600">{balance.earned_LEAVE}</p>
    </div>
    <div>
      <p className="font-medium">Sick</p>
      <p className="text-red-600">{balance.sick_LEAVE}</p>
    </div>
    <div>
      <p className="font-medium">Optional</p>
      <p className="text-purple-600">{balance.optional_LEAVE}</p>
    </div>
  </div>
</div>

  );
};

export default LeaveBalance;
