import React, { useEffect, useState } from "react";
import api from "../api/api";
import { useMessage } from "../context/MessageContext";
import { useAuth } from "../context/AuthContext";

export default function ExitRequests() {
  const { user } = useAuth(); // logged-in manager
  const { showMessage } = useMessage();

  const [exitRequests, setExitRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch manager's team exit requests
  useEffect(() => {
    const fetchExitRequests = async () => {
      if (!user?.empId) return;

      setLoading(true);
      try {
        const res = await api.get(`/exit-requests?managerId=${user.empId}`);
        setExitRequests(res.data || []);
      } catch (err) {
        console.error("Error fetching exit requests:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchExitRequests();
  }, [user]);

  // Approve / Reject action
  const handleExitAction = async (id, action) => {
    try {
      await api.post(`/exits/${id}/${action.toLowerCase()}`);
      setExitRequests((prev) =>
        prev.map((req) => (req.id === id ? { ...req, status: action } : req))
      );
      showMessage(
        `Exit request ${action.toLowerCase()}!`,
        action === "Approved" ? "success" : "error"
      );
    } catch (err) {
      console.error("Error updating exit:", err);
    }
  };

  if (loading) return <p>Loading exit requests...</p>;

  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Exit Requests</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Employee</th>
            <th className="p-2 border">Exit Date</th>
            <th className="p-2 border">Remarks</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Action</th>
          </tr>
        </thead>
        <tbody>
          {exitRequests.length === 0 ? (
            <tr>
              <td colSpan="5" className="p-2 text-center">
                No exit requests found.
              </td>
            </tr>
          ) : (
            exitRequests.map((req) => (
              <tr key={req.id}>
                <td className="p-2 border">{req.employee}</td>
                <td className="p-2 border">
                  {new Date(req.exitDate).toLocaleDateString()}
                </td>
                <td className="p-2 border">{req.remarks}</td>
                <td className="p-2 border">{req.status}</td>
                <td className="p-2 border space-x-2">
                  {req.status === "PENDING" && (
                    <>
                      <button
                        className="bg-green-600 text-white px-2 py-1 rounded"
                        onClick={() => handleExitAction(req.id, "Approved")}
                      >
                        Approve
                      </button>
                      <button
                        className="bg-red-600 text-white px-2 py-1 rounded"
                        onClick={() => handleExitAction(req.id, "Rejected")}
                      >
                        Reject
                      </button>
                    </>
                  )}
                  {req.status !== "PENDING" && "-"}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
