import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import api from "../api/api"; // centralized axios instance
import "./ApplyLeave.css";

export default function ApplyLeave() {
  const { user } = useAuth(); // logged-in user
  const [leaveType, setLeaveType] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [reason, setReason] = useState("");
  const [status, setStatus] = useState("idle"); // idle, loading, success, error
  const [message, setMessage] = useState("");

  // Disable past dates
  const today = new Date().toISOString().split("T")[0];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setMessage("");

    if (!leaveType || !fromDate || !toDate || !reason.trim()) {
      setMessage("All fields are required.");
      setStatus("error");
      return;
    }

    const payload = {
      employeeId: user?.empId, // make sure empId matches backend field
      leaveType,
      startDate: fromDate,
      endDate: toDate,
      reason,
      status: "PENDING", // optional: backend may set this automatically
    };

    try {
      await api.post("/leaves/apply", payload); // session-based auth
      setMessage("Leave applied successfully.");
      setStatus("success");

      // Reset form
      setLeaveType("");
      setFromDate("");
      setToDate("");
      setReason("");
    } catch (err) {
      console.error("Error applying leave:", err);
      setMessage(err.response?.data?.message || "Failed to apply leave.");
      setStatus("error");
    }
  };

  return (
    <div className="apply-leave-container p-4">
      <h2 className="text-xl font-bold mb-4">Apply Leave</h2>

      {message && (
        <p className={`mb-2 ${status === "error" ? "text-red-600" : "text-green-600"}`}>
          {message}
        </p>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block">Leave Type</label>
          <select
            className="border p-2 rounded w-full"
            value={leaveType}
            onChange={(e) => setLeaveType(e.target.value)}
            disabled={status === "loading"}
            required
          >
            <option value="">Select</option>
            <option value="CASUAL_LEAVE">Casual Leave</option>
            <option value="SICK_LEAVE">Sick Leave</option>
            <option value="OPTIONAL_LEAVE">Optional Leave</option>
            <option value="EARNED_LEAVE">Earned Leave</option>
          </select>
        </div>

        <div>
          <label className="block">From Date</label>
          <input
            type="date"
            className="border p-2 rounded w-full"
            value={fromDate}
            onChange={(e) => setFromDate(e.target.value)}
            disabled={status === "loading"}
            min={today} // prevent past dates
            required
          />
        </div>

        <div>
          <label className="block">To Date</label>
          <input
            type="date"
            className="border p-2 rounded w-full"
            value={toDate}
            onChange={(e) => setToDate(e.target.value)}
            disabled={status === "loading"}
            min={fromDate || today} // cannot select before fromDate
            required
          />
        </div>

        <div>
          <label className="block">Reason</label>
          <textarea
            className="border p-2 rounded w-full"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            disabled={status === "loading"}
            rows={3}
            required
          />
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          disabled={status === "loading"}
        >
          {status === "loading" ? "Submitting..." : "Apply"}
        </button>
      </form>
    </div>
  );
}
