import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const API_BASE = "http://localhost:8080";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const { login: setAuthUser } = useAuth();

  const redirectByRole = (role) => {
    switch (role) {
      case "ROLE_ADMIN":
        navigate("/admindashboard", { replace: true });
        break;
      case "ROLE_MANAGER":
        navigate("/managerdashboard", { replace: true });
        break;
      case "ROLE_EMPLOYEE":
      default:
        navigate("/employeedashboard", { replace: true });
        break;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await axios.post(
        `${API_BASE}/api/auth/login`,
        {
          // ðŸ‘‡ MUST match backend field names
          userEmail: email,
          userPassword: password,
        },
        { withCredentials: true }
      );

      const data = res.data; // { message, email, role }

      if (data?.role) {
        // store in auth context + localStorage
        setAuthUser({ email: data.email, role: data.role ,id: data.id});
        redirectByRole(data.role);
      } else {
        setError(data?.message || "Unexpected login response");
      }
    } catch (err) {
      // 400 likely from @Valid failing or bad field names; 401 for bad creds
      const msg =
        err?.response?.data?.message ||
        (err?.response?.status === 401
          ? "Invalid credentials. Please try again."
          : "Login failed. Please check your details.");
      setError(msg);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded p-6 w-96">
        <h2 className="text-xl font-bold mb-4">Login</h2>

        {error && <p className="text-red-500 mb-2">{error}</p>}

        <div className="mb-4">
          <label className="block text-gray-700">Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value.trim())}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600">
          Login
        </button>
      </form>
    </div>
  );
}
