import { useState } from "react";

export default function HistoryTabs() {
  const [tab, setTab] = useState("leave");

  // Static demo data
  const leaves = [
    {
      id: 1,
      status: "APPROVED",
      totalDays: 3,
      segments: [{ type: "Casual", start: "2025-08-25", end: "2025-08-27" }],
      remarks: "Family function",
      createdAt: "2025-08-01T10:30:00Z"
    }
  ];

  const attendance = {
    "2025-08-18": { checkIn: "09:00", checkOut: "17:30", totalHours: 8.5, autoLeave: false },
    "2025-08-19": { checkIn: "09:15", checkOut: "17:10", totalHours: 8, autoLeave: false },
    "2025-08-20": { checkIn: null, checkOut: null, totalHours: null, autoLeave: true }
  };

  const exits = [
    {
      id: 101,
      status: "Pending",
      intendedDate: "2025-09-01",
      remarks: "Moving abroad",
      createdAt: "2025-08-15T14:00:00Z"
    }
  ];

  return (
    <div>
      <div style={{ display: "flex", gap: 8, marginBottom: 8 }}>
        <TabButton active={tab === "leave"} onClick={() => setTab("leave")}>
          Leave History
        </TabButton>
        <TabButton active={tab === "attendance"} onClick={() => setTab("attendance")}>
          Attendance
        </TabButton>
        <TabButton active={tab === "exit"} onClick={() => setTab("exit")}>
          Exit Requests
        </TabButton>
      </div>

      {tab === "leave" && <LeaveTable leaves={leaves} />}
      {tab === "attendance" && <AttendanceTable attendance={attendance} />}
      {tab === "exit" && <ExitTable exits={exits} />}
    </div>
  );
}

function TabButton({ active, children, onClick }) {
  return (
    <button
      onClick={onClick}
      style={{
        padding: "8px 12px",
        borderRadius: 8,
        border: "1px solid #ddd",
        background: active ? "#e0f2fe" : "#fff",
        cursor: "pointer"
      }}
    >
      {children}
    </button>
  );
}

function LeaveTable({ leaves }) {
  if (!leaves.length) return <div>No leaves yet.</div>;
  return (
    <div style={{ overflowX: "auto" }}>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <Th>ID</Th>
            <Th>Status</Th>
            <Th>Total Days</Th>
            <Th>Segments</Th>
            <Th>Remarks</Th>
            <Th>Created</Th>
          </tr>
        </thead>
        <tbody>
          {leaves.map((l) => (
            <tr key={l.id}>
              <Td>{l.id}</Td>
              <Td>{l.status}</Td>
              <Td>{l.totalDays}</Td>
              <Td>
                {l.segments.map((s, i) => (
                  <div key={i}>
                    {s.type}: {s.start} → {s.end}
                  </div>
                ))}
              </Td>
              <Td>{l.remarks}</Td>
              <Td>{new Date(l.createdAt).toLocaleString()}</Td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function AttendanceTable({ attendance }) {
  const rows = Object.entries(attendance).sort((a, b) => a[0].localeCompare(b[0]));
  if (!rows.length) return <div>No attendance yet.</div>;
  return (
    <div style={{ overflowX: "auto" }}>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <Th>Date</Th>
            <Th>Check-In</Th>
            <Th>Check-Out</Th>
            <Th>Total Hours</Th>
            <Th>Auto Leave</Th>
          </tr>
        </thead>
        <tbody>
          {rows.map(([d, rec]) => (
            <tr key={d}>
              <Td>{d}</Td>
              <Td>{rec.checkIn || "-"}</Td>
              <Td>{rec.checkOut || "-"}</Td>
              <Td>{rec.totalHours ?? "-"}</Td>
              <Td>{rec.autoLeave ? "Yes" : "No"}</Td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function ExitTable({ exits }) {
  if (!exits.length) return <div>No exit requests yet.</div>;
  return (
    <div style={{ overflowX: "auto" }}>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <Th>ID</Th>
            <Th>Status</Th>
            <Th>Intended Date</Th>
            <Th>Remarks</Th>
            <Th>Created</Th>
          </tr>
        </thead>
        <tbody>
          {exits.map((e) => (
            <tr key={e.id}>
              <Td>{e.id}</Td>
              <Td>{e.status}</Td>
              <Td>{e.intendedDate}</Td>
              <Td>{e.remarks}</Td>
              <Td>{new Date(e.createdAt).toLocaleString()}</Td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function Th({ children }) {
  return (
    <th style={{ textAlign: "left", borderBottom: "1px solid #e5e7eb", padding: "8px" }}>
      {children}
    </th>
  );
}
function Td({ children }) {
  return (
    <td style={{ borderBottom: "1px solid #f1f5f9", padding: "8px", verticalAlign: "top" }}>
      {children}
    </td>
  );
}
