import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import api from "../api/api"; 
import "./ApplyLeave.css";
import LeaveBalance from "./LeaveBalance";

export default function ApplyLeave() {
  const { user } = useAuth(); 
  const [leaveType, setLeaveType] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [remarks, setRemarks] = useState(""); 
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");

  // ðŸ‘‡ this will force re-render LeaveBalance when changed
  const [refreshBalance, setRefreshBalance] = useState(0);

  const today = new Date().toISOString().split("T")[0];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setMessage("");

    if (!leaveType || !fromDate || !toDate || !remarks.trim()) { 
      setMessage("All fields are required.");
      setStatus("error");
      return;
    }
    
    const payload = {
      employeeId: user?.empId, 
      leaveType,
      startDate: fromDate,
      endDate: toDate,
      remarks,                 
      status: "PENDING",        
    };

    try {
      await api.post("/leaves/apply", payload, { withCredentials: true }); 
      setMessage("Leave applied successfully.");
      setStatus("success");

      // clear form
      setLeaveType("");
      setFromDate("");
      setToDate("");
      setRemarks(""); 

      // ðŸ‘‡ trigger LeaveBalance refresh
      setRefreshBalance(prev => prev + 1);

    } catch (err) {
      console.error("Error applying leave:", err);
      setMessage(err.response?.data?.message || "Failed to apply leave.");
      setStatus("error");
    }
  };

  return (
    <div className="apply-leave-container p-4">
      <LeaveBalance employeeId={user.empId} refreshTrigger={refreshBalance} />

      <h2 className="text-xl font-bold mb-4">Apply Leave</h2>

      {message && (
        <p className={`mb-2 ${status === "error" ? "text-red-600" : "text-green-600"}`}>
          {message}
        </p>
      )}

      {/* form same as before */}
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Leave type */}
        <div>
          <label className="block">Leave Type</label>
          <select
            className="border p-2 rounded w-full"
            value={leaveType}
            onChange={(e) => setLeaveType(e.target.value)}
            disabled={status === "loading"}
            required
          >
            <option value="">Select</option>
            <option value="CASUAL_LEAVE">Casual Leave</option>
            <option value="SICK_LEAVE">Sick Leave</option>
            <option value="OPTIONAL_LEAVE">Optional Leave</option>
            <option value="EARNED_LEAVE">Earned Leave</option>
          </select>
        </div>

        {/* From date */}
        <div>
          <label className="block">From Date</label>
          <input
            type="date"
            className="border p-2 rounded w-full"
            value={fromDate}
            onChange={(e) => setFromDate(e.target.value)}
            disabled={status === "loading"}
            min={today}
            required
          />
        </div>

        {/* To date */}
        <div>
          <label className="block">To Date</label>
          <input
            type="date"
            className="border p-2 rounded w-full"
            value={toDate}
            onChange={(e) => setToDate(e.target.value)}
            disabled={status === "loading"}
            min={fromDate || today} 
            required
          />
        </div>

        {/* Remarks */}
        <div>
          <label className="block">Remarks</label>
          <textarea
            className="border p-2 rounded w-full"
            value={remarks} 
            onChange={(e) => setRemarks(e.target.value)}
            disabled={status === "loading"}
            rows={3}
            required
          />
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          disabled={status === "loading"}
        >
          {status === "loading" ? "Submitting..." : "Apply"}
        </button>
      </form>
    </div>
  );
}
