import React, { useEffect, useState } from "react";
import api from "../api";

export default function YearlyAttendance() {
  const [report, setReport] = useState([]);
  const [year, setYear] = useState(new Date().getFullYear());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchReport = async () => {
    setLoading(true);
    setError("");
    try {
      const res = await api.post(`/admin/getReport?year=${year}`);
      setReport(res.data);
    } catch (err) {
      setError("Failed to load report");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReport();
  }, []);

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Yearly Attendance Report ({year})</h2>

      <div className="mb-4">
        <input
          type="number"
          value={year}
          onChange={(e) => setYear(e.target.value)}
          className="border px-2 py-1 mr-2"
        />
        <button
          onClick={fetchReport}
          className="bg-blue-500 text-white px-4 py-1 rounded hover:bg-blue-600"
        >
          Fetch Report
        </button>
      </div>

      {loading && <p>Loading...</p>}
      {error && <p className="text-red-500">{error}</p>}

      <div className="overflow-x-auto">
        <table className="table-auto border-collapse border border-gray-400 w-full">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-2 py-1">Month</th>
              <th className="border px-2 py-1">Employee ID</th>
              <th className="border px-2 py-1">Present Days</th>
              <th className="border px-2 py-1">Half Days</th>
              <th className="border px-2 py-1">Absent Days</th>
            </tr>
          </thead>
          <tbody>
            {report.map((monthData, idx) => {
              const [month, employees] = Object.entries(monthData)[0]; // month => employees list
              return employees.map((emp, empIdx) => {
                const [empId, stats] = Object.entries(emp)[0];
                const [presentMap, halfDayMap, absentMap] = stats;

                return (
                  <tr key={`${month}-${empId}-${empIdx}`}>
                    <td className="border px-2 py-1">{month}</td>
                    <td className="border px-2 py-1">{empId}</td>
                    <td className="border px-2 py-1">
                      {presentMap?.["PRESENT"] ?? 0}
                    </td>
                    <td className="border px-2 py-1">
                      {halfDayMap?.["HALF_DAY"] ?? 0}
                    </td>
                    <td className="border px-2 py-1">
                      {absentMap?.["ABSENT"] ?? 0}
                    </td>
                  </tr>
                );
              });
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}
