import React, { useEffect, useState } from "react";
import api from "../api/api";
import "./ExitRequests.css";

export default function ExitRequests({ managerId }) {
  const [exitRequests, setExitRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");


  useEffect(() => {
    const fetchExitRequests = async () => {
      setLoading(true);
      setMessage("");
      try {
        const res = await api.get(`manager/exit-requests?managerId=${managerId}`);
        setExitRequests(res.data || []);
      } catch (err) {
        console.error("Error fetching exit requests:", err);
        setMessage("Failed to fetch exit requests.");
      } finally {
        setLoading(false);
      }
    };

    if (managerId) fetchExitRequests();
  }, [managerId]);

  return (
    <div className="exit-requests-container">
      <h2>Team Exit Requests</h2>

      {loading && <p className="exit-requests-loading">Loading exit requests...</p>}
      {message && <p className="exit-requests-message error">{message}</p>}

      {!loading && exitRequests.length === 0 && (
        <p className="exit-requests-loading">No exit requests found.</p>
      )}

      {exitRequests.length > 0 && (
        <table className="exit-requests-table">
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Exit Date</th>
              <th>Remarks</th>
            </tr>
          </thead>
          <tbody>
            {exitRequests.map((req) => (
              <tr key={req.requestId}>
                <td>{req.employeeName}</td>
                <td>{new Date(req.exitDate).toLocaleDateString()}</td>
                <td>{req.remarks}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
