import React, { useState, useEffect } from "react";
import { Routes, Route, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import ApplyLeave from "./ApplyLeave";
import Attendance from "./Attendence";
import ExitRequest from "./ExitRequest";
import LeaveBalance from "./LeaveBalance";
import { getuserDetails } from "../api/api"; // Assuming this function exists

export default function EmployeeDashboard() {
  const { user, logout } = useAuth();

  // Home component inside EmployeeDashboard
  const Home = ({ user }) => {
    const [employee, setEmployee] = useState({});

    useEffect(() => {
      if (user?.email) {
        getuserDetails(user.email)
          .then((response) => {
            setEmployee(response.data);
          })
          .catch((error) => {
            console.error("Error fetching employee details:", error);
          });
      }
    }, [user]);

    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          padding: "50px",
          height: "100%",
          backgroundColor: "#eef2f6",
          fontFamily: "'Segoe UI', 'Roboto', 'Helvetica Neue', Arial, sans-serif"
        }}
      >
        <div
          style={{
            backgroundColor: "#ffffff",
            border: "1px solid #d3dce0",
            borderRadius: "8px",
            padding: "40px",
            width: "100%",
            maxWidth: "700px",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)"
          }}
        >
          <h1
            style={{
              fontSize: "2rem",
              color: "#007bff",
              fontWeight: "600",
              textAlign: "center",
              marginBottom: "30px",
              borderBottom: "2px solid #e1e7f0",
              paddingBottom: "15px"
            }}
          >
            üìú Employee Dashboard
          </h1>
          <p
            style={{
              fontSize: "1.2rem",
              color: "#007bff",
              textAlign: "center",
              lineHeight: "1.6",
              marginBottom: "30px"
            }}
          >
            Welcome, {employee.userRole} üßë‚Äçüíª !!!
          </p>

          <div
            style={{
              display: "grid",
              gridTemplateColumns: "1fr 2fr",
              gap: "20px",
              padding: "20px",
              backgroundColor: "#f4f7f9",
              borderRadius: "6px"
            }}
          >
            <p style={labelStyle}>Employee ID:</p>
            <p style={valueStyle}>{employee.employeeId}</p>

            <p style={labelStyle}>Email Address:</p>
            <p style={valueStyle}>{employee.userEmail}</p>

            <p style={labelStyle}>Account Role:</p>
            <p style={valueStyle}>{employee.userRole}</p>
          </div>

          <p
            style={{
              marginTop: "40px",
              fontSize: "1rem",
              color: "#718096",
              textAlign: "center"
            }}
          >
            Please use the navigation bar to manage your attendance, leave requests, and exit requests.
          </p>
        </div>
      </div>
    );
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
      {/* Navbar */}
      <nav
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          padding: "12px 20px",
          background: "#2563eb",
          color: "#fff"
        }}
      >
        <div style={{ display: "flex", gap: 20 }}>
          <Link style={linkStyle} to="/employeedashboard/apply-leave">
            Apply Leave
          </Link>
          <Link style={linkStyle} to="/employeedashboard/attendance">
            Attendance
          </Link>
          <Link style={linkStyle} to="/employeedashboard/exit">
            Exit Request
          </Link>
        </div>

        <div style={{ display: "flex", alignItems: "center", gap: 20 }}>
          {user && (
            <span style={{ fontWeight: "500" }}>
              Welcome, {user.email}! ,{user.empId}
            </span>
          )}
          <button onClick={logout} style={buttonStyle}>
            Logout
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <main style={{ flex: 1, padding: 20, background: "#f9fafb" }}>
        <Routes>
          <Route path="apply-leave" element={<><LeaveBalance /><ApplyLeave /></>} />
          <Route path="attendance" element={<Attendance />} />
          <Route path="exit" element={<ExitRequest />} />
          <Route path="*" element={<Home user={user} />} />
        </Routes>
      </main>
    </div>
  );
}

// Styles
const linkStyle = {
  color: "#fff",
  textDecoration: "none",
  fontWeight: "500"
};

const buttonStyle = {
  padding: "8px 16px",
  backgroundColor: "#1e40af",
  color: "#fff",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  fontWeight: "500"
};

const labelStyle = {
  fontWeight: "500",
  color: "#4a5568"
};

const valueStyle = {
  color: "#1e40af",
  fontWeight: "600"
};
