import React, { useState, useEffect, useCallback } from "react";
import { useAuth } from "../context/AuthContext";
import api from "../api/api"; // centralized axios instance
import "./ExitRequest.css";

export default function ExitRequestTabs() {
  const { user } = useAuth();
  const [tab, setTab] = useState("create"); // "create" or "view"
  const [exitRequests, setExitRequests] = useState([]);
  const [remarks, setRemarks] = useState("");
  const [exitDate, setExitDate] = useState("");
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");

  // Disable past dates
  const today = new Date().toISOString().split("T")[0];

  // Reset message and status on tab change
  useEffect(() => {
    setMessage("");
    setStatus("idle");
  }, [tab]);

  // Helper function to get auth headers
  const getAuthHeaders = () => {
    if (!user || !user.email || !user.password) return {};
    return {
      auth: { username: user.email, password: user.password },
      headers: { "Content-Type": "application/json" },
    };
  };

  // Fetch all exit requests for the current employee
  const fetchMyRequests = useCallback(async () => {
    if (!user || !user.empId) return;

    setStatus("loading");
    setMessage("");

    try {
      const res = await api.get(`/exit-requests/get/my`, getAuthHeaders());
      const data = res.data.exitRequests || [];
      setExitRequests(data);
      setStatus("success");
    } catch (err) {
      console.error("Failed to fetch exit requests:", err);
      setMessage(err.response?.data?.message || "Failed to fetch exit requests.");
      setStatus("error");
    }
  }, [user]);

  useEffect(() => {
    if (tab === "view") {
      fetchMyRequests();
    }
  }, [tab, fetchMyRequests]);

  // Submit a new exit request
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setMessage("");

    if (!exitDate || !remarks.trim()) {
      setMessage("Exit date and remarks are required.");
      setStatus("error");
      return;
    }

    const requestData = {
      employeeId: user.empId, // âœ… include employee ID
      exitDate,
      remarks,
    };

    try {
      await api.post("/exit-requests/create", requestData, getAuthHeaders());
      setMessage("Exit request submitted successfully.");
      setStatus("success");
      setExitDate("");
      setRemarks("");
      await fetchMyRequests();
    } catch (err) {
      console.error("Error submitting exit request:", err);
      setMessage(err.response?.data?.message || "Failed to submit exit request.");
      setStatus("error");
    }
  };

  // Cancel a pending request
  const handleCancel = async (requestId) => {
    setStatus("loading");
    setMessage("");
    try {
      await api.put(`/exit-requests/cancel/${requestId}`, null, getAuthHeaders());
      setMessage("Exit request canceled successfully.");
      setStatus("success");
      await fetchMyRequests();
    } catch (err) {
      console.error("Error canceling exit request:", err);
      setMessage(err.response?.data?.message || "Failed to cancel exit request.");
      setStatus("error");
    }
  };

  return (
    <div className="exit-request-tabs-container">
      <div className="tab-buttons">
        <button
          className={tab === "create" ? "active" : ""}
          onClick={() => setTab("create")}
        >
          Create Exit Request
        </button>
        <button
          className={tab === "view" ? "active" : ""}
          onClick={() => setTab("view")}
        >
          View Exit Requests
        </button>
      </div>

      {message && <div className={`status-message ${status}`}>{message}</div>}

      {tab === "create" && (
        <form onSubmit={handleSubmit} className="exit-form">
          <div className="form-group">
            <label htmlFor="exitDate">Intended Exit Date</label>
            <input
              type="date"
              id="exitDate"
              value={exitDate}
              onChange={(e) => setExitDate(e.target.value)}
              disabled={status === "loading"}
              min={today} // Prevent past dates
            />
          </div>
          <div className="form-group">
            <label htmlFor="remarks">Remarks</label>
            <textarea
              id="remarks"
              value={remarks}
              onChange={(e) => setRemarks(e.target.value)}
              disabled={status === "loading"}
              rows={3}
            />
          </div>
          <button type="submit" disabled={status === "loading"} className="submit-button">
            {status === "loading" ? "Submitting..." : "Submit"}
          </button>
        </form>
      )}

      {tab === "view" && (
        <table className="exit-request-table">
          <thead>
            <tr>
              <th>Request ID</th>
              <th>Exit Date</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {exitRequests.length === 0 ? (
              <tr>
                <td colSpan="4">No exit requests found.</td>
              </tr>
            ) : (
              exitRequests.map((req) => (
                <tr key={req.requestId}>
                  <td>{req.requestId}</td>
                  <td>{new Date(req.exitDate).toLocaleDateString()}</td>
                  <td>{req.requestStatus}</td>
                  <td>
                    {req.requestStatus === "PENDING" ? (
                      <button
                        className="cancel-button"
                        onClick={() => handleCancel(req.requestId)}
                        disabled={status === "loading"}
                      >
                        Cancel
                      </button>
                    ) : (
                      "-"
                    )}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}
