import React, { useEffect, useState } from "react";
import axios from "axios";
import api from "../api/api";

export default function EmployeeLeaveHistory({ employeeId }) {
  const [leaves, setLeaves] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch leave history
  const fetchLeaves = async () => {
    try {
      const response = await api.get(
        `/leaves/${employeeId}`,
        { withCredentials: true }
      );
      setLeaves(response.data);
    } catch (err) {
      setError("Failed to fetch leave history");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLeaves();
  }, [employeeId]);

  const handleCancel = async (requestId) => {
    if (!window.confirm("Are you sure you want to cancel this leave?")) return;

    try {
      await api.post(
        `/leaves/cancel/${requestId}`,
        {},
        { withCredentials: true }
      );
      alert("Leave cancelled successfully!");
      fetchLeaves();
    } catch (err) {
      console.error("Error cancelling leave:", err);
      alert("Failed to cancel leave. Try again.");
    }
  };

  if (loading) return <p>Loading leave history...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ margin: "20px" }}>
      <h2>Leave History for Employee {employeeId}</h2>
      <table
        border="1"
        cellPadding="8"
        cellSpacing="0"
        style={{ width: "100%", borderCollapse: "collapse" }}
      >
        <thead>
          <tr style={{ backgroundColor: "#f4f4f4" }}>
            <th>Request ID</th>
            <th>Leave Type</th>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Remarks</th>
            <th>Status</th>
            <th>Rejection Reason</th>
            <th>Action</th> {/* 👈 New column */}
          </tr>
        </thead>
        <tbody>
          {leaves.map((leave) => (
            <tr key={leave.requestId}>
              <td>{leave.requestId}</td>
              <td>{leave.leaveType.replace("_", " ")}</td>
              <td>{leave.startDate}</td>
              <td>{leave.endDate}</td>
              <td>{leave.remarks}</td>
              <td
                style={{
                  color:
                    leave.leaveStatus === "ACCEPTED"
                      ? "green"
                      : leave.leaveStatus === "REJECTED"
                      ? "red"
                      : "orange",
                  fontWeight: "bold",
                }}
              >
                {leave.leaveStatus}
              </td>
              <td>{leave.rejectionReason || "-"}</td>
              <td>
                {leave.leaveStatus === "PENDING" && (
                  <button
                    onClick={() => handleCancel(leave.requestId)}
                    style={{
                      backgroundColor: "red",
                      color: "white",
                      border: "none",
                      padding: "5px 10px",
                      borderRadius: "5px",
                      cursor: "pointer",
                    }}
                  >
                    Cancel
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
