import React, { useState } from "react";

export default function ApplyLeave() {
  const [leaveType, setLeaveType] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [reason, setReason] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Leave applied:\nType: ${leaveType}\nFrom: ${fromDate}\nTo: ${toDate}\nReason: ${reason}`);
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Apply Leave</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        
        <div>
          <label className="block">Leave Type</label>
          <select
            className="border p-2 rounded w-full"
            value={leaveType}
            onChange={(e) => setLeaveType(e.target.value)}
            required
          >
            <option value="">Select</option>
            <option value="Casual">Casual Leave</option>
            <option value="Sick">Sick Leave</option>
            <option value="Optional">Optional Leave</option>
            <option value="">Earned Leave</option>
          </select>
        </div>

        <div>
          <label className="block">From Date</label>
          <input
            type="date"
            className="border p-2 rounded w-full"
            value={fromDate}
            onChange={(e) => setFromDate(e.target.value)}
            required
          />
        </div>

        <div>
          <label className="block">To Date</label>
          <input
            type="date"
            className="border p-2 rounded w-full"
            value={toDate}
            onChange={(e) => setToDate(e.target.value)}
            required
          />
        </div>

        <div>
          <label className="block">Reason</label>
          <textarea
            className="border p-2 rounded w-full"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            required
          />
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Apply
        </button>
      </form>
    </div>
  );
}
