import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

export default function Attendance() {
  const { user } = useAuth(); // get logged-in user
  const [attendance, setAttendance] = useState(null);
  const [loading, setLoading] = useState(false);
  const [fetching, setFetching] = useState(true); // loading state for initial fetch
  const [error, setError] = useState(null);

  const employeeId = user?.employeeId;

  // Fetch today's attendance
  useEffect(() => {
    if (!user || !employeeId) return; // wait until user is loaded

    const today = new Date().toISOString().split("T")[0];

    const fetchAttendance = async () => {
      setFetching(true);
      setError(null);
      try {
        const res = await axios.get(
          `http://localhost:8080/api/attendance/${employeeId}/${today}`
        );
        setAttendance(res.data);
      } catch (err) {
        console.warn("No attendance found for today", err);
        setAttendance(null);
      } finally {
        setFetching(false);
      }
    };

    fetchAttendance();
  }, [user, employeeId]);

  // Punch attendance
  const handlePunch = async () => {
    if (!employeeId) return;
    setLoading(true);
    setError(null);

    try {
      // Send raw integer as JSON
      await axios.post(
        "http://localhost:8080/api/attendance/mark",
        JSON.stringify(employeeId),
        { headers: { "Content-Type": "application/json" } }
      );

      // Refresh attendance after marking
      const today = new Date().toISOString().split("T")[0];
      const updated = await axios.get(
        `http://localhost:8080/api/attendance/${employeeId}/${today}`
      );
      setAttendance(updated.data);
    } catch (err) {
      console.error("Error marking attendance:", err);
      setError("Failed to mark attendance. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  if (!user) return <p>Loading user data...</p>; // wait for auth

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Attendance</h2>

      {fetching ? (
        <p>Loading attendance...</p>
      ) : attendance ? (
        <div className="space-y-2">
          <p>
            <strong>Date:</strong> {attendance.date}
          </p>
          <p>
            <strong>Status:</strong> {attendance.attendenceStatus}
          </p>
          <p>
            <strong>Check-In:</strong> {attendance.checkIn || "-"}
          </p>
          <p>
            <strong>Check-Out:</strong> {attendance.checkOut || "-"}
          </p>
        </div>
      ) : (
        <p>No attendance marked today.</p>
      )}

      {error && <p className="text-red-500 mt-2">{error}</p>}

      <button
        onClick={handlePunch}
        disabled={loading}
        className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:bg-gray-400"
      >
        {loading ? "Saving..." : "Mark Attendance"}
      </button>
    </div>
  );
}
