import React, { useEffect, useState } from "react";
import axios from "axios";

export default function YearlyAttendance() {
  const [attendanceStats, setAttendanceStats] = useState([]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const employeeId = localStorage.getItem("employeeId"); // stored after login
       const response = await axios.get(
  `http://localhost:8080/api/admin/getReport`,
  {
    params: { year: 2025 },   
    withCredentials: true    
  }
);


        // Ensure stats is always an array
        const statsData = response.data;
        if (Array.isArray(statsData)) {
          setAttendanceStats(statsData);
        } else if (statsData) {
          setAttendanceStats([statsData]); // wrap single object into array
        } else {
          setAttendanceStats([]); // empty if null
        }
      } catch (error) {
        console.error("Error fetching yearly attendance:", error);
        setAttendanceStats([]);
      }
    };

    fetchStats();
  }, []);

  return (
    <div>
      <h2>Yearly Attendance</h2>
      {attendanceStats.length === 0 ? (
        <p>No attendance stats available.</p>
      ) : (
        <table border="1" style={{ borderCollapse: "collapse", width: "100%" }}>
          <thead>
            <tr>
              <th>Month</th>
              <th>Days Present</th>
              <th>Days Absent</th>
            </tr>
          </thead>
          <tbody>
            {attendanceStats.map((stat, index) => (
              <tr key={index}>
                <td>{stat.month || "N/A"}</td>
                <td>{stat.presentDays || 0}</td>
                <td>{stat.absentDays || 0}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
