import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useMessage } from '../context/MessageContext';
import '../styles/Login.css'; // Make sure you have this CSS file

const LoginPage = () => {
  const [userEmail, setUserEmail] = useState('');
  const [userPassword, setUserPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});

  const { login } = useAuth();
  const { showMessage } = useMessage();

  const validateForm = () => {
    const newErrors = {};
    if (!userEmail) newErrors.userEmail = 'Email is required.';
    if (!userPassword) newErrors.userPassword = 'Password is required.';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      const result = await login(userEmail, userPassword);
      if (!result.success) {
        showMessage(result.message, 'error');
      }
    } else {
      showMessage('Please correct the form errors.', 'error');
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit} className="login-form">
        <h2>Login</h2>
        <div className={`message-container ${errors.userEmail || errors.userPassword ? 'error' : ''}`}>
          {Object.values(errors).map((error, index) => (
            <div key={index} className="error-text">{error}</div>
          ))}
        </div>
        <div className="form-group">
          <label htmlFor="userEmail">Email</label>
          <input
            type="text"
            id="userEmail"
            value={userEmail}
            onChange={(e) => setUserEmail(e.target.value)}
            className={errors.userEmail ? 'input-error' : ''}
          />
        </div>
        <div className="form-group">
          <label htmlFor="userPassword">Password</label>
          <input
            type={showPassword ? 'text' : 'password'}
            id="userPassword"
            value={userPassword}
            onChange={(e) => setUserPassword(e.target.value)}
            className={errors.userPassword ? 'input-error' : ''}
          />
        </div>
        <div className="form-checkbox">
          <input
            type="checkbox"
            id="showPassword"
            checked={showPassword}
            onChange={() => setShowPassword(!showPassword)}
          />
          <label htmlFor="showPassword">Show Password</label>
        </div>
        <button type="submit" className="login-button">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
