import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import "./ExitRequest.css";

const API_BASE_URL = "http://localhost:8080/api/exit-requests";

export default function ExitRequest() {
  const { user } = useAuth();
  const [exitRequest, setExitRequest] = useState(null);
  const [remarks, setRemarks] = useState("");
  const [intendedDate, setIntendedDate] = useState("");
  const [status, setStatus] = useState("idle"); // 'idle', 'loading', 'success', 'error'
  const [message, setMessage] = useState("");

  // Fetch the employee's existing exit request on component load
  useEffect(() => {
    if (!user || !user.empId) return;

    const fetchExitRequest = async () => {
      setStatus("loading");
      setMessage("");
      try {
        const res = await axios.get(`${API_BASE_URL}/get/my`, {
          withCredentials: true,
        });
        // Assuming the backend returns an array, we get the first one or null
        const requestData = res.data.exitRequests?.[0];
        setExitRequest(requestData);
        // If an existing request is found, pre-populate the form
        if (requestData) {
          setIntendedDate(requestData.intendedDate);
          setRemarks(requestData.remarks);
        }
        setStatus("success");
      } catch (err) {
        if (err.response && err.response.status === 404) {
          // No existing request found, which is a normal state
          setExitRequest(null);
          setStatus("idle");
        } else {
          console.error("Failed to fetch exit request:", err);
          setMessage("Failed to fetch exit request. Please try again later.");
          setStatus("error");
        }
      }
    };

    fetchExitRequest();
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setMessage("");

    if (!intendedDate || !remarks.trim()) {
      setMessage("Intended exit date and remarks are required.");
      setStatus("error");
      return;
    }

    const requestData = {
      employeeId: user.empId,
      intendedDate: intendedDate,
      remarks: remarks,
    };

    try {
      await axios.post(`${API_BASE_URL}/create`, requestData, {
        headers: { "Content-Type": "application/json" },
        withCredentials: true,
      });
      setMessage("Exit request submitted successfully.");
      setStatus("success");
      // Re-fetch the data to get the newly created request
      fetchExitRequest();
    } catch (err) {
      console.error("Error submitting exit request:", err);
      setMessage(err.response?.data?.message || "Failed to submit exit request.");
      setStatus("error");
    }
  };

  const handleCancel = async () => {
    if (!exitRequest) return;
    setStatus("loading");
    setMessage("");

    try {
      await axios.put(`${API_BASE_URL}/cancel/${exitRequest.requestId}`, {}, {
        withCredentials: true,
      });
      setMessage("Exit request canceled successfully.");
      setStatus("success");
      // Clear the exit request from state
      setExitRequest(null);
      setIntendedDate("");
      setRemarks("");
    } catch (err) {
      console.error("Error canceling exit request:", err);
      setMessage(err.response?.data?.message || "Failed to cancel exit request.");
      setStatus("error");
    }
  };

  const hasPendingOrApprovedRequest = exitRequest && (
    exitRequest.requestStatus === "PENDING" || exitRequest.requestStatus === "ACCEPTED"
  );
  
  const isCancellable = exitRequest && exitRequest.requestStatus === "PENDING";

  return (
    <div className="exit-request-container">
      <div className="exit-request-card">
        <h2 className="card-title">Exit Request</h2>

        {status === "loading" && <div className="status-message loading">Loading...</div>}
        {message && <div className={`status-message ${status}`}>{message}</div>}

        {/* Display existing request status if it exists */}
        {exitRequest && (
          <div className="existing-request-info">
            <p>
              Your last request status:{" "}
              <b className={`status-badge status-${exitRequest.requestStatus.toLowerCase()}`}>
                {exitRequest.requestStatus}
              </b>
            </p>
            <p>
              Submitted on:{" "}
              <b>
                {new Date(exitRequest.requestSubmittedAt).toLocaleDateString()}
              </b>
            </p>
            <p>
              Intended Date: <b>{new Date(exitRequest.intendedDate).toLocaleDateString()}</b>
            </p>
          </div>
        )}

        {!hasPendingOrApprovedRequest && (
          <form onSubmit={handleSubmit} className="exit-form">
            <div className="form-group">
              <label htmlFor="intendedDate">Intended Exit Date</label>
              <input
                type="date"
                id="intendedDate"
                value={intendedDate}
                onChange={(e) => setIntendedDate(e.target.value)}
                disabled={status === "loading"}
              />
            </div>
            <div className="form-group">
              <label htmlFor="remarks">Remarks (required)</label>
              <textarea
                id="remarks"
                value={remarks}
                onChange={(e) => setRemarks(e.target.value)}
                disabled={status === "loading"}
                rows={3}
              />
            </div>
            <button
              type="submit"
              disabled={status === "loading"}
              className="submit-button"
            >
              {status === "loading" ? "Submitting..." : "Submit New Request"}
            </button>
          </form>
        )}
        
        {/* Button to cancel a PENDING request */}
        {isCancellable && (
          <button
            onClick={handleCancel}
            disabled={status === "loading"}
            className="cancel-button"
          >
            {status === "loading" ? "Canceling..." : "Cancel Request"}
          </button>
        )}
      </div>
    </div>
  );
}
