import React, { useState, useEffect } from "react";
import axios from "axios";

export default function ExitRequests() {
  const [exitDate, setExitDate] = useState("");
  const [remarks, setRemarks] = useState("");
  const [requests, setRequests] = useState([]);
  const [role, setRole] = useState(""); // employee / manager / admin
  const [message, setMessage] = useState("");

  // Fetch role dynamically if you already store it after login
  useEffect(() => {
    const storedRole = localStorage.getItem("role");
    if (storedRole) setRole(storedRole);
  }, []);

  const submitExitRequest = async () => {
    if (!exitDate || !remarks) {
      setMessage("Exit date and remarks are mandatory.");
      return;
    }
    try {
      const res = await axios.post("http://localhost:8080/api/exit-requests", {
        exitDate,
        remarks,
      });
      setMessage(res.data.message || "Exit request submitted.");
      setExitDate("");
      setRemarks("");
      fetchRequests();
    } catch (err) {
      console.error(err);
      setMessage("Error submitting exit request.");
    }
  };

  const fetchRequests = async () => {
    try {
      const res = await axios.get("http://localhost:8080/api/exit-requests");
      setRequests(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleManagerRecommendation = async (id) => {
    try {
      const res = await axios.put(
        `http://localhost:8080/api/exit-requests/${id}/recommend`
      );
      setMessage(res.data.message);
      fetchRequests();
    } catch (err) {
      console.error(err);
    }
  };

  const handleAdminApproval = async (id) => {
    try {
      const res = await axios.put(
        `http://localhost:8080/api/exit-requests/${id}/approve`
      );
      setMessage(res.data.message);
      fetchRequests();
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Exit Requests</h2>

      {role === "EMPLOYEE" && (
        <div className="mb-6 p-4 border rounded bg-gray-50">
          <h3 className="font-semibold mb-2">Submit Exit Request</h3>
          <input
            type="date"
            value={exitDate}
            onChange={(e) => setExitDate(e.target.value)}
            className="border p-2 mr-2"
          />
          <input
            type="text"
            value={remarks}
            onChange={(e) => setRemarks(e.target.value)}
            placeholder="Remarks"
            className="border p-2 mr-2"
          />
          <button
            onClick={submitExitRequest}
            className="bg-blue-500 text-white px-3 py-1 rounded"
          >
            Submit
          </button>
        </div>
      )}

      {message && <p className="text-green-600 mb-4">{message}</p>}

      <h3 className="font-semibold mb-2">All Exit Requests</h3>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-100">
            <th className="border p-2">Employee</th>
            <th className="border p-2">Exit Date</th>
            <th className="border p-2">Remarks</th>
            <th className="border p-2">Status</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {requests.map((req) => (
            <tr key={req.id}>
              <td className="border p-2">{req.employeeName}</td>
              <td className="border p-2">{req.exitDate}</td>
              <td className="border p-2">{req.remarks}</td>
              <td className="border p-2">{req.status}</td>
              <td className="border p-2">
                {role === "MANAGER" && req.status === "PENDING" && (
                  <button
                    onClick={() => handleManagerRecommendation(req.id)}
                    className="bg-yellow-500 text-white px-2 py-1 rounded"
                  >
                    Recommend
                  </button>
                )}
                {role === "ADMIN" && req.status !== "APPROVED" && (
                  <button
                    onClick={() => handleAdminApproval(req.id)}
                    className="bg-green-600 text-white px-2 py-1 rounded ml-2"
                  >
                    Approve
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
