import React from "react";
import { Routes, Route, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import ApplyLeave from "./ApplyLeave";
import Attendance from "./Attendence";
import ExitRequest from "./ExitRequest";
import HistoryTabs from "./HistoryTabs";

export default function EmployeeDashboard() {
  // Use the useAuth hook to get the current user's data and the logout function
  const { user, logout } = useAuth();

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
      {/* Navbar with dynamic user info and logout button */}
      <nav style={{
        display: "flex",
        justifyContent: "space-between", // Distributes items evenly
        alignItems: "center",
        padding: "12px 20px",
        background: "#2563eb",
        color: "#fff",
      }}>
        {/* Navigation Links */}
        <div style={{ display: "flex", gap: 20 }}>
          <Link style={linkStyle} to="/employeedashboard/apply-leave">Apply Leave</Link>
          <Link style={linkStyle} to="/employeedashboard/attendance">Attendance</Link>
          <Link style={linkStyle} to="/employeedashboard/exit">Exit Request</Link>
          <Link style={linkStyle} to="/employeedashboard/history">My History</Link>
        </div>

        {/* User Info and Logout Button */}
        <div style={{ display: "flex", alignItems: "center", gap: 20 }}>
          {user && (
            <span style={{ fontWeight: "500" }}>Welcome, {user.email}!,{user.empId}</span>
          )}
          <button
            onClick={logout}
            style={buttonStyle}
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Page Content */}
      <main style={{ flex: 1, padding: 20, background: "#f9fafb" }}>
        <Routes>
          <Route path="apply-leave" element={<ApplyLeave />} />
          <Route path="attendance" element={<Attendance />} />
          <Route path="exit" element={<ExitRequest />} />
          <Route path="history" element={<HistoryTabs />} />
          {/* Default welcome page */}
          <Route path="*" element={<div>Welcome Employee! Select a menu option above.</div>} />
        </Routes>
      </main>
    </div>
  );
}

// Styles for the links
const linkStyle = {
  color: "#fff",
  textDecoration: "none",
  fontWeight: "500",
};

// Styles for the button
const buttonStyle = {
  padding: "8px 16px",
  backgroundColor: "#1e40af", // A darker blue
  color: "#fff",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  fontWeight: "500"
};
