import { useState } from "react";
import { todayISO } from "../utils/date";

export default function ExitRequest() {
  const [date, setDate] = useState(todayISO());
  const [remarks, setRemarks] = useState("");
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  // for now, no dynamic exit data — just static
  const hasExit = false;

  const handleSubmit = (e) => {
    e.preventDefault();
    setMsg("");

    if (!remarks.trim()) {
      setMsg("Remarks are required.");
      return;
    }

    setLoading(true);
    setTimeout(() => {
      setMsg("Exit submitted successfully (static demo).");
      setLoading(false);
    }, 1000); // simulate API delay
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: "grid", gap: 10, maxWidth: 520 }}>
      {/* Static Exit Info */}
      <div style={{ background: "#eef2ff", border: "1px solid #c7d2fe", padding: 8, borderRadius: 6 }}>
        Exit status: <b>Approved</b> — intended date: <b>2025-09-01</b>
      </div>

      <div>
        <label>Intended Exit Date</label>
        <input
          type="date"
          value={date}
          onChange={e => setDate(e.target.value)}
          disabled={hasExit || loading}
          style={s.input}
        />
      </div>
      <div>
        <label>Remarks (required)</label>
        <textarea
          value={remarks}
          onChange={e => setRemarks(e.target.value)}
          disabled={hasExit || loading}
          rows={3}
          style={s.input}
        />
      </div>
      <button type="submit" disabled={hasExit || loading} style={s.btn}>
        {loading ? "Submitting..." : "Submit Exit Request"}
      </button>
      {msg && <div style={{ color: msg.includes("successfully") ? "green" : "crimson" }}>{msg}</div>}
      <small>After submitting exit, new leave applications are blocked.</small>
    </form>
  );
}

const s = {
  input: { width: "100%", padding: "8px 10px", border: "1px solid #ddd", borderRadius: 6 },
  btn: {
    background: "#16a34a",
    color: "#fff",
    border: "none",
    padding: "10px 14px",
    borderRadius: 6,
    cursor: "pointer"
  }
};
