/**
 * 
 */
// MessageDisplay.js
import React from "react";
import { useMessage } from "./MessageContext"; // since both are in the same folder

const MessageDisplay = () => {
  const { message, messageType } = useMessage();

  if (!message) return null; // nothing to show

  return (
    <div
      style={{
        padding: "10px",
        margin: "10px 0",
        borderRadius: "5px",
        color: messageType === "error" ? "#721c24" : "#155724",
        backgroundColor: messageType === "error" ? "#f8d7da" : "#d4edda",
        border: messageType === "error" ? "1px solid #f5c6cb" : "1px solid #c3e6cb",
      }}
    >
      {message}
    </div>
  );
};

export default MessageDisplay;
