import React, { useEffect, useState } from "react";
import axios from "axios";

export default function YearlyAttendance() {
  const [attendanceStats, setAttendanceStats] = useState([]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8080/api/admin/getReport",
          {
            params: { year: 2025 }, // or dynamically pick year
            withCredentials: true,  // include session cookie
          }
        );

        const rawData = response.data; // backend array

        // Transform into something easier for UI
        const parsed = rawData.map((employeeObj) => {
          const employeeId = Object.keys(employeeObj)[0];
          const monthsData = employeeObj[employeeId]; // list of {month: days}
          const formattedMonths = monthsData.map((m) => {
            const month = Object.keys(m)[0];
            const days = m[month];
            return { month, presentDays: days };
          });
          return { employeeId, months: formattedMonths };
        });

        setAttendanceStats(parsed);
      } catch (error) {
        console.error("Error fetching yearly attendance:", error);
        setAttendanceStats([]);
      }
    };

    fetchStats();
  }, []);

  return (
    <div>
      <h2>Yearly Attendance Report</h2>
      {attendanceStats.length === 0 ? (
        <p>No attendance stats available.</p>
      ) : (
        attendanceStats.map((emp) => (
          <div key={emp.employeeId} style={{ marginBottom: "20px" }}>
            <h3>Employee ID: {emp.employeeId}</h3>
            <table
              border="1"
              style={{ borderCollapse: "collapse", width: "100%" }}
            >
              <thead>
                <tr>
                  <th>Month</th>
                  <th>Days Present</th>
                </tr>
              </thead>
              <tbody>
                {emp.months.map((m, idx) => (
                  <tr key={idx}>
                    <td>{m.month}</td>
                    <td>{m.presentDays}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))
      )}
    </div>
  );
}
