import React, { useState, useEffect, useCallback } from "react";
import { useAuth } from "../context/AuthContext";
import api from "../api/api";
import "./ExitRequest.css";

export default function ExitRequestTabs() {
  const { user } = useAuth();
  const [tab, setTab] = useState("create"); // tabs: create or view
  const [exitRequests, setExitRequests] = useState([]);
  const [exitDate, setExitDate] = useState("");
  const [remarks, setRemarks] = useState("");
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");

  // Auth headers
  const getAuthHeaders = () => {
    if (!user || !user.email || !user.password) return {};
    return {
      auth: {
        username: user.email,
        password: user.password,
      },
      headers: { "Content-Type": "application/json" },
    };
  };

  // Fetch exit requests for the employee
  const fetchExitRequests = useCallback(async () => {
    if (!user || !user.empId) return;
    setStatus("loading");
    try {
      const res = await api.get(`/exit-requests/get/my`, getAuthHeaders());
      setExitRequests(res.data.exitRequests || []);
      setStatus("success");
    } catch (err) {
      console.error("Failed to fetch exit requests:", err);
      setMessage(err.response?.data?.message || "Failed to fetch exit requests");
      setStatus("error");
    }
  }, [user]);

  useEffect(() => {
    if (tab === "view") fetchExitRequests();
  }, [tab, fetchExitRequests]);

  // Submit new exit request
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!exitDate || !remarks.trim()) {
      setMessage("Exit date and remarks are required");
      setStatus("error");
      return;
    }
    setStatus("loading");
    try {
      await api.post(
        "/exit-requests/create",
        {
          employeeId: user.empId, 
          exitDate,
          remarks,
        },
        getAuthHeaders()
      );
      setMessage("Exit request submitted successfully");
      setStatus("success");
      setExitDate("");
      setRemarks("");
      setTab("view");
      await fetchExitRequests();
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || "Failed to submit exit request");
      setStatus("error");
    }
  };

  const handleCancel = async (requestId) => {
    setStatus("loading");
    try {
      await api.put(`/exit-requests/cancel/${requestId}`, null, getAuthHeaders());
      setMessage("Exit request canceled successfully");
      setStatus("success");
      await fetchExitRequests();
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || "Failed to cancel exit request");
      setStatus("error");
    }
  };

  return (
    <div className="exit-request-container">
      {/* Tabs */}
      <div className="tabs">
        <button
          className={tab === "create" ? "active-tab" : ""}
          onClick={() => setTab("create")}
        >
          Create Exit Request
        </button>
        <button
          className={tab === "view" ? "active-tab" : ""}
          onClick={() => setTab("view")}
        >
          View Exit Requests
        </button>
      </div>

      {/* Status Message */}
      {message && <div className={`status-message ${status}`}>{message}</div>}

      {/* Create Tab */}
      {tab === "create" && (
        <form onSubmit={handleSubmit} className="exit-form">
          <div className="form-group">
            <label htmlFor="exitDate">Exit Date</label>
            <input
              type="date"
              id="exitDate"
              value={exitDate}
              onChange={(e) => setExitDate(e.target.value)}
              disabled={status === "loading"}
            />
          </div>
          <div className="form-group">
            <label htmlFor="remarks">Remarks</label>
            <textarea
              id="remarks"
              value={remarks}
              onChange={(e) => setRemarks(e.target.value)}
              disabled={status === "loading"}
              rows={3}
            />
          </div>
          <button type="submit" disabled={status === "loading"} className="submit-button">
            {status === "loading" ? "Submitting..." : "Submit"}
          </button>
        </form>
      )}

      {/* View Tab */}
      {tab === "view" && (
        <div className="exit-requests-list">
          {status === "loading" && <p>Loading...</p>}
          {exitRequests.length === 0 && <p>No exit requests found.</p>}
          {exitRequests.map((req) => (
            <div key={req.requestId} className="exit-request-card">
              <p>
                <b>Exit Date:</b> {new Date(req.exitDate).toLocaleDateString()}
              </p>
              <p>
                <b>Status:</b>{" "}
                <span className={`status-badge status-${req.requestStatus?.toLowerCase()}`}>
                  {req.requestStatus}
                </span>
              </p>
              <p>
                <b>Remarks:</b> {req.remarks}
              </p>
              {req.adminRemarks && (
                <p>
                  <b>Admin Remarks:</b> {req.adminRemarks}
                </p>
              )}
              {req.requestStatus === "PENDING" && (
                <button
                  className="cancel-button"
                  onClick={() => handleCancel(req.requestId)}
                  disabled={status === "loading"}
                >
                  Cancel
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
