import React from "react";
import { Link, Routes, Route } from "react-router-dom";
import ApplyLeave from "./ApplyLeave";
import Attendance from "./Attendence";
import { useMessage } from "../context/MessageContext";

// Subcomponents (you can also move them to separate files later)
function LeaveRequests({ handleLeaveAction, leaveRequests }) {
  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Employee Leave Requests</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Employee</th>
            <th className="p-2 border">Type</th>
            <th className="p-2 border">From</th>
            <th className="p-2 border">To</th>
            <th className="p-2 border">Remarks</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Action</th>
          </tr>
        </thead>
        <tbody>
          {leaveRequests.map((req) => (
            <tr key={req.id}>
              <td className="p-2 border">{req.employee}</td>
              <td className="p-2 border">{req.type}</td>
              <td className="p-2 border">{req.from}</td>
              <td className="p-2 border">{req.to}</td>
              <td className="p-2 border">{req.remarks}</td>
              <td className="p-2 border">{req.status}</td>
              <td className="p-2 border space-x-2">
                <button
                  className="bg-green-600 text-white px-2 py-1 rounded"
                  onClick={() => handleLeaveAction(req.id, "Approved")}
                >
                  Approve
                </button>
                <button
                  className="bg-red-600 text-white px-2 py-1 rounded"
                  onClick={() => handleLeaveAction(req.id, "Rejected")}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function ExitRequests({ handleExitAction, exitRequests }) {
  return (
    <div>
      <h2 className="text-xl font-semibold mb-2">Exit Requests</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2 border">Employee</th>
            <th className="p-2 border">Exit Date</th>
            <th className="p-2 border">Remarks</th>
            <th className="p-2 border">Status</th>
            <th className="p-2 border">Action</th>
          </tr>
        </thead>
        <tbody>
          {exitRequests.map((req) => (
            <tr key={req.id}>
              <td className="p-2 border">{req.employee}</td>
              <td className="p-2 border">{req.exitDate}</td>
              <td className="p-2 border">{req.remarks}</td>
              <td className="p-2 border">{req.status}</td>
              <td className="p-2 border space-x-2">
                <button
                  className="bg-green-600 text-white px-2 py-1 rounded"
                  onClick={() => handleExitAction(req.id, "Approved")}
                >
                  Approve
                </button>
                <button
                  className="bg-red-600 text-white px-2 py-1 rounded"
                  onClick={() => handleExitAction(req.id, "Rejected")}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default function ManagerDashboard() {
  const { showMessage } = useMessage();

useEffect(() => {
  const fetchData = async () => {
    const leaveRes = await api.get("/leaves/pending"); 
    setLeaveRequests(leaveRes.data);

    const exitRes = await api.get("/exits/pending"); 
    setExitRequests(exitRes.data);
  };
  fetchData();
}, []);


  const [exitRequests, setExitRequests] = React.useState([
    { id: 1, employee: "Charlie", exitDate: "2025-09-01", remarks: "Career shift", status: "Pending" },
  ]);

  // ✅ Leave Actions
  const handleLeaveAction = (id, action) => {
    setLeaveRequests((prev) =>
      prev.map((req) => (req.id === id ? { ...req, status: action } : req))
    );
    showMessage(`Leave request ${action.toLowerCase()}!`, action === "Approved" ? "success" : "error");
  };

  // ✅ Exit Actions
  const handleExitAction = (id, action) => {
    setExitRequests((prev) =>
      prev.map((req) => (req.id === id ? { ...req, status: action } : req))
    );
    showMessage(`Exit request ${action.toLowerCase()}!`, action === "Approved" ? "success" : "error");
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
      <nav style={{ 
          display: "flex", 
          gap: 20, 
          padding: "12px 20px", 
          background: "#2563eb", 
          color: "#fff" 
        }}>
      {/* ✅ Navbar */}
      <nav className="flex space-x-4 bg-gray-800 text-white p-3 rounded">
        <Link style={linkStyle} to="/managerdashboard/leave-requests"> Leave Requests </Link>
        <Link style={linkStyle} to="/managerdashboard/exit-requests"> Exit Requests </Link>
        <Link style={linkStyle} to="/managerdashboard/attendance" > Attendance </Link>
        <Link style={linkStyle} to="/managerdashboard/apply-leave"> Apply Leave </Link>
      </nav>
        </nav>
      {/* ✅ Routes */}
      <div className="p-4">
        <Routes>
          <Route
            path="leave-requests"
            element={<LeaveRequests leaveRequests={leaveRequests} handleLeaveAction={handleLeaveAction} />}
          />
          <Route
            path="exit-requests"
            element={<ExitRequests exitRequests={exitRequests} handleExitAction={handleExitAction} />}
          />
          <Route path="attendance" element={<Attendance />} />
          <Route
            path="apply-leave"
            element={
              <div>
                <h2 className="text-xl font-semibold mb-2">Apply for Leave (Manager)</h2>
                <p className="text-sm text-gray-500 mb-2">(Requires Admin approval)</p>
                <ApplyLeave />
              </div>
            }
          />
        </Routes>
      </div>
    </div>
  );
}
const linkStyle = {
  color: "#fff",
  textDecoration: "none",
  fontWeight: "500"
};
