import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import './Attendance.css';

// Centralize the API base URL
const API_BASE_URL = "http://localhost:8080/api/attendance";

// A utility function to format dates
const formatDate = (dateString) => {
  if (!dateString) return "N/A";
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', {
    weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'
  });
};

export default function Attendance() {
  const { user } = useAuth();
  const [attendance, setAttendance] = useState(null);
  const [status, setStatus] = useState('idle'); // 'idle', 'loading', 'success', 'error'
  const [errorMessage, setErrorMessage] = useState('');

  const employeeId = user?.empId; // Use empId from auth context

  // Fetch today's attendance on component mount
  useEffect(() => {
    if (!user || !employeeId) return;

    const fetchTodayAttendance = async () => {
      setStatus('loading');
      setErrorMessage('');
      const today = new Date().toISOString().split("T")[0];

      try {
        const response = await axios.get(
          `${API_BASE_URL}/${employeeId}/${today}`,
          { withCredentials: true }
        );
        setAttendance(response.data);
        setStatus('success');
      } catch (err) {
        // API returns a 404 if no attendance is found for the day
        if (err.response && err.response.status === 404) {
          setAttendance(null);
          setStatus('idle');
        } else {
          console.error("Failed to fetch attendance:", err);
          setErrorMessage("Failed to fetch attendance data.");
          setStatus('error');
        }
      }
    };

    fetchTodayAttendance();
  }, [user, employeeId]);

  // Handle the punch-in/punch-out action
  const handlePunch = async () => {
    if (!employeeId) {
      setErrorMessage("User data is not available.");
      return;
    }

    setStatus('loading');
    setErrorMessage('');

    try {
      await axios.post(
        `${API_BASE_URL}/mark`,
        { employeeId: employeeId },
        { headers: { "Content-Type": "application/json" }, withCredentials: true }
      );
      
      // After a successful punch, refetch the data to get the updated status
      const today = new Date().toISOString().split("T")[0];
      const updatedRes = await axios.get(
        `${API_BASE_URL}/${employeeId}/${today}`,
        { withCredentials: true }
      );
      setAttendance(updatedRes.data);
      setStatus('success');

    } catch (err) {
      console.error("Error marking attendance:", err.response || err);
      setErrorMessage(err.response?.data || "Failed to mark attendance. Please try again.");
      setStatus('error');
    }
  };

  const isCheckedIn = attendance?.checkIn && !attendance.checkOut;
  const isCheckedOut = attendance?.checkIn && attendance.checkOut;

  return (
    <div className="attendance-card-container">
      <div className="attendance-card">
        <h2 className="card-title">Attendance for {formatDate(new Date())}</h2>
        <div className="status-indicator">
          <span className={`status-dot ${isCheckedIn ? 'dot-in' : (isCheckedOut ? 'dot-out' : 'dot-none')}`}></span>
          <p className="status-text">
            {isCheckedIn ? 'You are currently punched in.' : (isCheckedOut ? 'You have punched out for today.' : 'Not punched in today.')}
          </p>
        </div>

        {/* Display fetched attendance details */}
        {attendance && (
          <div className="attendance-details">
            <div className="detail-item">
              <span className="detail-label">Check-In:</span>
              <span className="detail-value">{attendance.checkIn || '-'}</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Check-Out:</span>
              <span className="detail-value">{attendance.checkOut || '-'}</span>
            </div>
          </div>
        )}

        {/* Action Button and Status Messages */}
        <div className="action-area">
          {status === 'loading' && <p className="loading-message">Processing...</p>}
          {status === 'error' && <p className="error-message">{errorMessage}</p>}
          
          <button
            onClick={handlePunch}
            disabled={status === 'loading' || isCheckedOut}
            className={`action-button ${isCheckedIn ? 'punch-out-button' : 'punch-in-button'}`}
          >
            {isCheckedIn ? 'Punch Out' : 'Punch In'}
          </button>
        </div>
      </div>
    </div>
  );
}
