import React, { useState, useEffect, useCallback } from "react";
import { useAuth } from "../context/AuthContext";
import api from "../api/api";
import "./ExitRequest.css";

export default function ExitRequest() {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState("create"); // 'create' or 'view'
  const [exitRequest, setExitRequest] = useState(null);
  const [remarks, setRemarks] = useState("");
  const [intendedDate, setIntendedDate] = useState("");
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");

  const getAuthHeaders = () => {
    if (!user || !user.email || !user.password) return {};
    return {
      auth: {
        username: user.email,
        password: user.password,
      },
      headers: { "Content-Type": "application/json" },
    };
  };

  // Fetch user's exit request
  const fetchExitRequest = useCallback(async () => {
    if (!user || !user.empId) return;

    setStatus("loading");
    setMessage("");

    try {
      const res = await api.get("/exit-requests/get/my", getAuthHeaders());
      const requestData = res.data.exitRequests?.[0] || null;

      if (!requestData) {
        setExitRequest(null);
        setIntendedDate("");
        setRemarks("");
        setMessage(`No exit requests found for employee with id ${user.empId}`);
        setStatus("success");
        return;
      }

      setExitRequest(requestData);
      setIntendedDate(requestData.intendedDate);
      setRemarks(requestData.remarks);
      setStatus("success");
      setMessage("");
    } catch (err) {
      console.error("Failed to fetch exit request:", err);
      setMessage(err.response?.data?.message || "Failed to fetch exit request.");
      setStatus("error");
    }
  }, [user]);

  useEffect(() => {
    fetchExitRequest();
  }, [fetchExitRequest]);

  // Submit a new exit request
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setMessage("");

    if (!intendedDate || !remarks.trim()) {
      setMessage("Intended exit date and remarks are required.");
      setStatus("error");
      return;
    }

    try {
      await api.post("/exit-requests/create", { intendedDate, remarks }, getAuthHeaders());
      setMessage("Exit request submitted successfully.");
      setStatus("success");
      fetchExitRequest();
      setActiveTab("view"); // Switch to view tab after creating
    } catch (err) {
      console.error("Error submitting exit request:", err);
      setMessage(err.response?.data?.message || "Failed to submit exit request.");
      setStatus("error");
    }
  };

  const handleCancel = async () => {
    if (!exitRequest) return;

    setStatus("loading");
    setMessage("");

    try {
      await api.put(`/exit-requests/cancel/${exitRequest.requestId}`, null, getAuthHeaders());
      setMessage("Exit request canceled successfully.");
      setStatus("success");
      fetchExitRequest();
    } catch (err) {
      console.error("Error canceling exit request:", err);
      setMessage(err.response?.data?.message || "Failed to cancel exit request.");
      setStatus("error");
    }
  };

  return (
    <div className="exit-request-container">
      <div className="tabs">
        <button
          className={activeTab === "create" ? "tab active" : "tab"}
          onClick={() => setActiveTab("create")}
        >
          Create Exit Request
        </button>
        <button
          className={activeTab === "view" ? "tab active" : "tab"}
          onClick={() => setActiveTab("view")}
        >
          View My Requests
        </button>
      </div>

      {message && <div className={`status-message ${status}`}>{message}</div>}

      {activeTab === "create" && (
        <form onSubmit={handleSubmit} className="exit-form">
          <div className="form-group">
            <label htmlFor="intendedDate">Intended Exit Date</label>
            <input
              type="date"
              id="intendedDate"
              value={intendedDate}
              onChange={(e) => setIntendedDate(e.target.value)}
              disabled={status === "loading"}
            />
          </div>
          <div className="form-group">
            <label htmlFor="remarks">Remarks (required)</label>
            <textarea
              id="remarks"
              value={remarks}
              onChange={(e) => setRemarks(e.target.value)}
              disabled={status === "loading"}
              rows={3}
            />
          </div>
          <button type="submit" disabled={status === "loading"} className="submit-button">
            {status === "loading" ? "Submitting..." : "Submit Request"}
          </button>
        </form>
      )}

      {activeTab === "view" && exitRequest && (
        <div className="existing-request-info">
          <p>
            Status: <b>{exitRequest.requestStatus}</b>
          </p>
          <p>
            Submitted on: <b>{new Date(exitRequest.requestSubmittedAt).toLocaleDateString()}</b>
          </p>
          <p>
            Intended Date: <b>{new Date(exitRequest.intendedDate).toLocaleDateString()}</b>
          </p>
          {exitRequest.requestStatus === "PENDING" && (
            <button onClick={handleCancel} disabled={status === "loading"} className="cancel-button">
              {status === "loading" ? "Canceling..." : "Cancel Request"}
            </button>
          )}
        </div>
      )}

      {activeTab === "view" && !exitRequest && <p>No exit requests found.</p>}
    </div>
  );
}
