import React, { useState, useEffect, useCallback } from "react";
import { useAuth } from "../context/AuthContext";
import api from "../api/api"; 
import LeaveBalance from "./LeaveBalance";
import "./ApplyLeave.css"; // use your provided CSS

export default function LeaveTabs() {
  const { user } = useAuth();
  const [tab, setTab] = useState("apply"); // "apply" or "view"
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [leaveType, setLeaveType] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [remarks, setRemarks] = useState("");
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");

  const today = new Date().toISOString().split("T")[0];

  useEffect(() => {
    setMessage("");
    setStatus("idle");
  }, [tab]);

  const fetchMyLeaves = useCallback(async () => {
    if (!user?.empId) return;
    setStatus("loading");
    setMessage("");

    try {
      const res = await api.get(`/leaves/${user.empId}`);
      setLeaveRequests(res.data || []);
      setStatus("success");
    } catch (err) {
      console.error("Failed to fetch leave requests:", err);
      setMessage(err.response?.data?.message || "Failed to fetch leave requests.");
      setStatus("error");
    }
  }, [user]);

  useEffect(() => {
    if (tab === "view") fetchMyLeaves();
  }, [tab, fetchMyLeaves]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setMessage("");

    if (!leaveType || !fromDate || !toDate || !remarks.trim()) {
      setMessage("All fields are required.");
      setStatus("error");
      return;
    }

    const payload = {
      employeeId: user.empId,
      leaveType,
      startDate: fromDate,
      endDate: toDate,
      remarks,
      leaveStatus: "PENDING",
    };

    try {
      await api.post("/leaves/apply", payload);
      setMessage("Leave applied successfully.");
      setStatus("success");
      setLeaveType("");
      setFromDate("");
      setToDate("");
      setRemarks("");
      await fetchMyLeaves();
    } catch (err) {
      console.error("Error applying leave:", err);
      setMessage(err.response?.data?.message || "Failed to apply leave.");
      setStatus("error");
    }
  };

  const handleCancel = async (requestId) => {
    setStatus("loading");
    setMessage("");

    try {
      await api.put(`/leaves/cancel/${requestId}`);
      setMessage("Leave cancelled successfully.");
      setStatus("success");
      await fetchMyLeaves();
    } catch (err) {
      console.error("Error cancelling leave:", err);
      setMessage(err.response?.data?.message || "Failed to cancel leave.");
      setStatus("error");
    }
  };

  return (
    <div className="apply-leave-container">
      <LeaveBalance employeeId={user.empId} />

      {/* Tabs */}
      <div className="tab-buttons mb-4" style={{ display: "flex", gap: "10px", marginBottom: "16px" }}>
        <button
          className={`attendance-button ${tab === "apply" ? "active" : ""}`}
          onClick={() => setTab("apply")}
        >
          Apply Leave
        </button>
        <button
          className={`attendance-button ${tab === "view" ? "active" : ""}`}
          onClick={() => setTab("view")}
        >
          View Leave Requests
        </button>
      </div>

      {message && (
        <div className={`status-message ${status === "error" ? "error" : "success"}`}>
          {message}
        </div>
      )}

      {tab === "apply" && (
        <form onSubmit={handleSubmit}>
          <div>
            <label>Leave Type</label>
            <select value={leaveType} onChange={(e) => setLeaveType(e.target.value)} disabled={status === "loading"}>
              <option value="">Select</option>
              <option value="CASUAL_LEAVE">Casual Leave</option>
              <option value="SICK_LEAVE">Sick Leave</option>
              <option value="OPTIONAL_LEAVE">Optional Leave</option>
              <option value="EARNED_LEAVE">Earned Leave</option>
            </select>
          </div>

          <div>
            <label>From Date</label>
            <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} min={today} disabled={status === "loading"} />
          </div>

          <div>
            <label>To Date</label>
            <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} min={fromDate || today} disabled={status === "loading"} />
          </div>

          <div>
            <label>Remarks</label>
            <textarea value={remarks} onChange={(e) => setRemarks(e.target.value)} rows={3} disabled={status === "loading"} />
          </div>

          <button type="submit" disabled={status === "loading"}>
            {status === "loading" ? "Submitting..." : "Apply"}
          </button>
        </form>
      )}

      {tab === "view" && (
        <div style={{ marginTop: "20px" }}>
          {leaveRequests.length === 0 ? (
            <p>No leave requests found.</p>
          ) : (
            <table style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead>
                <tr>
                  <th style={{ border: "1px solid #d1d5db", padding: "8px" }}>Leave Type</th>
                  <th style={{ border: "1px solid #d1d5db", padding: "8px" }}>From</th>
                  <th style={{ border: "1px solid #d1d5db", padding: "8px" }}>To</th>
                  <th style={{ border: "1px solid #d1d5db", padding: "8px" }}>Status</th>
                  <th style={{ border: "1px solid #d1d5db", padding: "8px" }}>Action</th>
                </tr>
              </thead>
              <tbody>
                {leaveRequests.map((leave) => (
                  <tr key={leave.requestId}>
                    <td style={{ border: "1px solid #d1d5db", padding: "8px" }}>{leave.leaveType}</td>
                    <td style={{ border: "1px solid #d1d5db", padding: "8px" }}>{new Date(leave.startDate).toLocaleDateString()}</td>
                    <td style={{ border: "1px solid #d1d5db", padding: "8px" }}>{new Date(leave.endDate).toLocaleDateString()}</td>
                    <td style={{ border: "1px solid #d1d5db", padding: "8px" }}>{leave.leaveStatus}</td>
                    <td style={{ border: "1px solid #d1d5db", padding: "8px", textAlign: "center" }}>
                      {leave.leaveStatus === "PENDING" && (
                        <button className="attendance-button" onClick={() => handleCancel(leave.requestId)} disabled={status === "loading"}>
                          Cancel
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
}
