import React, { useState, useEffect } from "react";

export default function Attendance() {
  const [checkInTime, setCheckInTime] = useState(null);
  const [checkOutTime, setCheckOutTime] = useState(null);

  // Load today's attendance from localStorage
  useEffect(() => {
    const today = new Date().toDateString();
    const savedDate = localStorage.getItem("attendanceDate");
    const savedCheckIn = localStorage.getItem("checkInTime");
    const savedCheckOut = localStorage.getItem("checkOutTime");

    if (savedDate === today) {
      setCheckInTime(savedCheckIn);
      setCheckOutTime(savedCheckOut);
    } else {
      // reset for a new day
      localStorage.setItem("attendanceDate", today);
      localStorage.removeItem("checkInTime");
      localStorage.removeItem("checkOutTime");
      setCheckInTime(null);
      setCheckOutTime(null);
    }
  }, []);

  const handleAttendance = () => {
    const time = new Date().toLocaleTimeString();

    if (!checkInTime) {
      // First click → set Check-In
      setCheckInTime(time);
      localStorage.setItem("checkInTime", time);
      localStorage.setItem("attendanceDate", new Date().toDateString());
    } else {
      // Any later click → update Check-Out
      setCheckOutTime(time);
      localStorage.setItem("checkOutTime", time);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Attendance</h2>

      <div className="space-y-4">
        <p><strong>Checked In:</strong> {checkInTime || "-"}</p>
        <p><strong>Checked Out:</strong> {checkOutTime || "-"}</p>

        <button
          onClick={handleAttendance}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {checkInTime ? "Update Check-Out" : "Check In"}
        </button>
      </div>
    </div>
  );
}
