import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

export default function Attendance() {
  const { user } = useAuth(); // ✅ get logged in user
  const [attendance, setAttendance] = useState(null);
  const [loading, setLoading] = useState(false);

  const employeeId = user?.employeeId; // you stored this in login context

  // ✅ Fetch today's attendance from backend
  useEffect(() => {
    if (!employeeId) return;

    const today = new Date().toISOString().split("T")[0]; // yyyy-mm-dd
    const fetchAttendance = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8080/api/attendance/${employeeId}/${today}`
        );
        setAttendance(res.data);
      } catch (err) {
        console.error("No attendance found for today", err);
        setAttendance(null);
      }
    };

    fetchAttendance();
  }, [employeeId]);

  // ✅ Punch attendance (check-in / check-out handled at backend)
  const handlePunch = async () => {
    if (!employeeId) return;
    setLoading(true);

    try {
      const res = await axios.post(
        "http://localhost:8080/api/attendance/mark",
        { employeeId } // backend only needs employeeId
      );

      console.log(res.data);
      // refresh attendance after marking
      const today = new Date().toISOString().split("T")[0];
      const updated = await axios.get(
        `http://localhost:8080/api/attendance/${employeeId}/${today}`
      );
      setAttendance(updated.data);
    } catch (err) {
      console.error("Error marking attendance:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Attendance</h2>

      {attendance ? (
        <div className="space-y-2">
          <p>
            <strong>Date:</strong> {attendance.date}
          </p>
          <p>
            <strong>Status:</strong> {attendance.attendenceStatus}
          </p>
          <p>
            <strong>Check-In:</strong> {attendance.checkIn || "-"}
          </p>
          <p>
            <strong>Check-Out:</strong> {attendance.checkOut || "-"}
          </p>
        </div>
      ) : (
        <p>No attendance marked today.</p>
      )}

      <button
        onClick={handlePunch}
        disabled={loading}
        className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:bg-gray-400"
      >
        {loading ? "Saving..." : "Mark Attendance"}
      </button>
    </div>
  );
}
