import {Routes, Route, Link } from "react-router-dom";
import ApplyLeave from "./ApplyLeave";
import Attendance from "./Attendence";
import ExitRequest from "./ExitRequest";
import HistoryTabs from "./HistoryTabs";

export default function EmployeeDashboard() {
  return (
    
      <div style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
        
        {/* ✅ Navbar */}
        <nav style={{ 
          display: "flex", 
          gap: 20, 
          padding: "12px 20px", 
          background: "#2563eb", 
          color: "#fff" 
        }}>
          <Link style={linkStyle} to="/employeedashboard/apply-leave">Apply Leave</Link>
          <Link style={linkStyle} to="/employeedashboard/attendance">Attendance</Link>
          <Link style={linkStyle} to="/employeedashboard/exit">Exit Request</Link>
          <Link style={linkStyle} to="/employeedashboard/history">My History</Link>
        </nav>

        {/* ✅ Page Content */}
        <main style={{ flex: 1, padding: 20, background: "#f9fafb" }}>
          <Routes>
            <Route path="apply-leave" element={<ApplyLeave />} />
            <Route path="attendance" element={<Attendance />} />
            <Route path="exit" element={<ExitRequest />} />
            <Route path="history" element={<HistoryTabs />} />
            {/* Default redirect page */}
            <Route path="*" element={<div>Welcome Employee! Select a menu option above.</div>} />
          </Routes>
        </main>
      </div>
    
  );
}

const linkStyle = {
  color: "#fff",
  textDecoration: "none",
  fontWeight: "500"
};
