import React, { useEffect, useState } from "react";
import axios from "axios";

export default function YearlyAttendance() {
  const [report, setReport] = useState([]);
  const [year, setYear] = useState(new Date().getFullYear());

  useEffect(() => {
    fetchReport(year);
  }, [year]);

  const fetchReport = async (selectedYear) => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/admin/getReport?year=${selectedYear}`
      );
      setReport(response.data);
    } catch (error) {
      console.error("Error fetching report", error);
    }
  };

  // helper to get month name
  const getMonthName = (monthNum) => {
    return new Date(2000, monthNum - 1, 1).toLocaleString("default", {
      month: "long",
    });
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-semibold mb-4">
        Yearly Attendance Report ({year})
      </h2>

      <div className="mb-4">
        <label className="mr-2">Select Year:</label>
        <input
          type="number"
          value={year}
          onChange={(e) => setYear(e.target.value)}
          className="border px-2 py-1 rounded"
        />
      </div>

      {report.length === 0 ? (
        <p>No data available</p>
      ) : (
        <div>
          {report.map((employeeData, idx) => {
            const employeeId = Object.keys(employeeData)[0]; // get empId
            const monthlyList = employeeData[employeeId]; // array of {month: days}

            // convert list of maps to single object
            const monthlyAttendance = {};
            monthlyList.forEach((item) => {
              const month = Object.keys(item)[0];
              const days = Object.values(item)[0];
              monthlyAttendance[month] = days;
            });

            return (
              <div
                key={idx}
                className="border rounded p-3 mb-4 shadow-sm bg-white"
              >
                <h3 className="text-lg font-bold mb-2">
                  Employee ID: {employeeId}
                </h3>
                <table className="w-full border">
                  <thead>
                    <tr className="bg-gray-100">
                      <th className="border px-2 py-1">Month</th>
                      <th className="border px-2 py-1">Working Days</th>
                    </tr>
                  </thead>
                  <tbody>
                    {Object.entries(monthlyAttendance).map(([month, days]) => (
                      <tr key={month}>
                        <td className="border px-2 py-1">{getMonthName(month)}</td>
                        <td className="border px-2 py-1 text-center">{days}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
