import React, { useState, useEffect, useCallback } from "react";
import { useAuth } from "../context/AuthContext";
import api from "../api/api"; // centralized axios instance
import "./ExitRequest.css";

export default function ExitRequest() {
  const { user } = useAuth();
  const [exitRequest, setExitRequest] = useState(null);
  const [remarks, setRemarks] = useState("");
  const [intendedDate, setIntendedDate] = useState("");
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");

  // Helper function to get basic auth headers
  const getAuthHeaders = () => {
    if (!user || !user.email || !user.password) return {};
    return {
      auth: {
        username: user.email,
        password: user.password,
      },
    };
  };

  // Fetch current user's exit request
  const fetchExitRequest = useCallback(async () => {
    if (!user || !user.empId) {
      setStatus("error");
      setMessage("User data is not available. Please log in again.");
      return;
    }

    setStatus("loading");
    setMessage("");

    try {
      const res = await api.get(`/exit-requests/get/my`, getAuthHeaders());
      const requestData = res.data.exitRequests?.[0] || null;
      setExitRequest(requestData);

      if (requestData) {
        setIntendedDate(requestData.intendedDate);
        setRemarks(requestData.remarks);
      } else {
        setIntendedDate("");
        setRemarks("");
      }

      setStatus("success");
    } catch (err) {
      console.error("Failed to fetch exit request:", err);
      setMessage("Failed to fetch exit request. Please try again later.");
      setStatus("error");
    }
  }, [user]);

  useEffect(() => {
    fetchExitRequest();
  }, [fetchExitRequest]);

  // Submit a new exit request
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setMessage("");

    if (!intendedDate || !remarks.trim()) {
      setMessage("Intended exit date and remarks are required.");
      setStatus("error");
      return;
    }

    const requestData = { intendedDate, remarks };

    try {
      await api.post("/exit-requests/create", requestData, {
        ...getAuthHeaders(),
        headers: { "Content-Type": "application/json" },
      });
      setMessage("Exit request submitted successfully.");
      setStatus("success");
      fetchExitRequest();
    } catch (err) {
      console.error("Error submitting exit request:", err);
      setMessage(err.response?.data?.message || "Failed to submit exit request.");
      setStatus("error");
    }
  };

  // Cancel an existing exit request
  const handleCancel = async () => {
    if (!exitRequest) return;

    setStatus("loading");
    setMessage("");

    try {
      await api.put(
        `/exit-requests/cancel/${exitRequest.requestId}`,
        null,
        getAuthHeaders()
      );
      setMessage("Exit request canceled successfully.");
      setStatus("success");
      fetchExitRequest();
    } catch (err) {
      console.error("Error canceling exit request:", err);
      setMessage(err.response?.data?.message || "Failed to cancel exit request.");
      setStatus("error");
    }
  };

  const hasPendingOrApprovedRequest = exitRequest && (
    exitRequest.requestStatus === "PENDING" || exitRequest.requestStatus === "ACCEPTED"
  );

  const isCancellable = exitRequest && exitRequest.requestStatus === "PENDING";

  return (
    <div className="exit-request-container">
      <div className="exit-request-card">
        <h2 className="card-title">Exit Request</h2>

        {status === "loading" && <div className="status-message loading">Loading...</div>}
        {message && <div className={`status-message ${status}`}>{message}</div>}

        {exitRequest && (
          <div className="existing-request-info">
            <p>
              Your last request status:{" "}
              <b className={`status-badge status-${exitRequest.requestStatus.toLowerCase()}`}>
                {exitRequest.requestStatus}
              </b>
            </p>
            <p>
              Submitted on:{" "}
              <b>{new Date(exitRequest.requestSubmittedAt).toLocaleDateString()}</b>
            </p>
            <p>
              Intended Date: <b>{new Date(exitRequest.intendedDate).toLocaleDateString()}</b>
            </p>
          </div>
        )}

        {!hasPendingOrApprovedRequest && (
          <form onSubmit={handleSubmit} className="exit-form">
            <div className="form-group">
              <label htmlFor="intendedDate">Intended Exit Date</label>
              <input
                type="date"
                id="intendedDate"
                value={intendedDate}
                onChange={(e) => setIntendedDate(e.target.value)}
                disabled={status === "loading"}
              />
            </div>
            <div className="form-group">
              <label htmlFor="remarks">Remarks (required)</label>
              <textarea
                id="remarks"
                value={remarks}
                onChange={(e) => setRemarks(e.target.value)}
                disabled={status === "loading"}
                rows={3}
              />
            </div>
            <button type="submit" disabled={status === "loading"} className="submit-button">
              {status === "loading" ? "Submitting..." : "Submit New Request"}
            </button>
          </form>
        )}

        {isCancellable && (
          <button onClick={handleCancel} disabled={status === "loading"} className="cancel-button">
            {status === "loading" ? "Canceling..." : "Cancel Request"}
          </button>
        )}
      </div>
    </div>
  );
}
